import React, { useState } from 'react';
import './TicTacToe.css';
import circle from '../assets/circle.png';
import cross from '../assets/cross.png';

let data = ['', '', '', '', '', '', '', '', ''];

function TicTacToe() {
    let [count, setCount] = useState(0);
    let [lock, setLock] = useState(false);

    const toggle = (e, num) => {
        if (lock || data[num]) {
            return; // Prevent overwriting or clicking after a win
        }

        if (count % 2 === 0) {
            e.target.innerHTML = `<img src="${cross}" alt="X">`; // Fixed syntax for image
            data[num] = 'x';
        } else {
            e.target.innerHTML = `<img src="${circle}" alt="O">`; // Fixed syntax for image
            data[num] = 'o';
        }

        setCount(count + 1); // Corrected state increment
        checkWin(); // Check winner after every move
    };

    const won = (winner) => {
        setLock(true); // Lock the board
        alert(`${winner.toUpperCase()} won the game!`); // Winner notification
    };

    const checkWin = () => {
        const winPatterns = [
            [0, 1, 2],
            [3, 4, 5],
            [6, 7, 8],
            [0, 3, 6],
            [1, 4, 7],
            [2, 5, 8],
            [0, 4, 8],
            [2, 4, 6],
        ];

        for (let pattern of winPatterns) {
            const [a, b, c] = pattern;
            if (data[a] && data[a] === data[b] && data[a] === data[c]) {
                won(data[a]); // Pass the winner to `won`
                return;
            }
        }
    };

    return (
        <div className='container'>
            <h1 className="title">Tic Tac Toe Game in <span>React</span></h1>
            <div className="board">
                <div className="row1">
                    <div className="boxes" onClick={(e) => { toggle(e, 0); }}></div>
                    <div className="boxes" onClick={(e) => { toggle(e, 1); }}></div>
                    <div className="boxes" onClick={(e) => { toggle(e, 2); }}></div>
                </div>

                <div className="row2">
                    <div className="boxes" onClick={(e) => { toggle(e, 3); }}></div>
                    <div className="boxes" onClick={(e) => { toggle(e, 4); }}></div>
                    <div className="boxes" onClick={(e) => { toggle(e, 5); }}></div>
                </div>

                <div className="row3">
                    <div className="boxes" onClick={(e) => { toggle(e, 6); }}></div>
                    <div className="boxes" onClick={(e) => { toggle(e, 7); }}></div>
                    <div className="boxes" onClick={(e) => { toggle(e, 8); }}></div>
                </div>
            </div>
            <button className="reset" onClick={() => window.location.reload()}>Reset</button>
        </div>
    );
}

export default TicTacToe;
