import React, { useState } from 'react';
import './TicTacToe.css';
import circle from '../assets/circle.png';
import cross from '../assets/cross.png';

function TicTacToe() {
    const [data, setData] = useState(Array(9).fill('')); // Board state
    const [count, setCount] = useState(0); // Move count
    const [lock, setLock] = useState(false); // Lock board when game is over

    const toggle = (index) => {
        if (lock || data[index] !== '') return; // Prevent further clicks on filled or locked boxes

        const newData = [...data];
        newData[index] = count % 2 === 0 ? 'x' : 'o'; // Assign 'x' or 'o'
        setData(newData);
        setCount(count + 1);

        checkWin(newData);
    };

    const checkWin = (board) => {
        const winningCombos = [
            [0, 1, 2],
            [3, 4, 5],
            [6, 7, 8],
            [0, 3, 6],
            [1, 4, 7],
            [2, 5, 8],
            [0, 4, 8],
            [2, 4, 6],
        ];

        for (let combo of winningCombos) {
            const [a, b, c] = combo;
            if (board[a] && board[a] === board[b] && board[b] === board[c]) {
                setLock(true); // Lock the board
                alert(`Player ${board[a]} wins!`);
                return;
            }
        }

        if (!board.includes('')) {
            alert('It\'s a draw!');
            setLock(true);
        }
    };

    const resetGame = () => {
        setData(Array(9).fill('')); // Clear the board
        setCount(0); // Reset move count
        setLock(false); // Unlock the board
    };

    return (
        <div className='container'>
            <h1 className="title">Tic Tac Toe Game in <span>React</span></h1>
            <div className="board">
                {data.map((value, index) => (
                    <div
                        key={index}
                        className="boxes"
                        onClick={() => toggle(index)}
                    >
                        {value === 'x' && <img src={cross} alt="cross" />}
                        {value === 'o' && <img src={circle} alt="circle" />}
                    </div>
                ))}
            </div>
            <button className="reset" onClick={resetGame}>Reset</button>
        </div>
    );
}

export default TicTacToe;
